package assignment;

public class Datatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a = 13;                        //upto 2 numbs only
		short b = 10099;                   //upto 5 numbs only 
		int c = 1234567689;               //upto 9 numbs only
		long d = 4555455558777887666L;   //upto 20 numbs including l/L only 
		float e = 2.44444444445555655555555555555555f; //doesnt have limit
		double f = 1000.785555555555555555555555599996;  //doesnt have limit                          
		char g = 'd';                                 //only one letter
		boolean h = true;    ///No cap should be used 
		String i = "hjhb";
		System.out.println("a:"+ a);
		System.out.println("short:"+ b);
		System.out.println("int:" + c);
		System.out.println("long:" + d);
		System.out.println("g:"+ e);
		System.out.println("double:" + f);
		System.out.println("char:" + g);
		System.out.println("boolean:" + h);
		System.out.println("string:" + i);

	}

}
