package javaprogram;

public class DAY2OPERATORS {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		  int a = 5; int b = 6; 
		  System.out.println(a + b);   //They are arithematic operators
		  System.out.println(a - b);
		  System.out.println(a * b); 
		  System.out.println(a / b); 
		  System.out.println(a % b);
		 
		int c =10;                    //they are unary operators
		System.out.println(++c);
		System.out.println(c++);
		
		System.out.println(a > b);  //they are relational operators
		System.out.println(a < b);
		System.out.println(a == b);
		System.out.println(a != b);
		
		int d =19;               // they are short hand operators
		// 
		d = d+5;
		d += 5;
		System.out.println(d);
	}

}
